import { relative } from 'path';
import { Link } from 'react-router-dom';
import { useEffect, useState, useRef  } from "react";

const AccountPage = () => {
    const [inputType, setInputType] = useState<string>('password');
    const [input2Type, setInput2Type] = useState<string>('passwordConfirm');
    const [emailValue, setEmailtValue] = useState<string>('');
    const [passwordValue, setPasswordtValue] = useState<string>('');
    const [passwordConfirmValue, setPasswordConfirmtValue] = useState<string>('');
    const inputRef = useRef<HTMLInputElement>(null);

    
    const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setEmailtValue(event.target.value);
      };

    const handlePasswordChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setPasswordtValue(event.target.value);
      };
      const handlePasswordConfirmChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        setPasswordConfirmtValue(event.target.value);
      };

    const handleClick = () => {
      setInputType(inputType === 'password' ? 'text' : 'password');
    };
    const handleClick2 = () => {
        setInputType(input2Type === 'password' ? 'text' : 'password');
      };
    return(
        <div className="banner-background">
            <img style={{width: "100%", height: "800px", position: "absolute", zIndex: -1}} src={require("..\\src\\components\\images\\wooden_background.jpg")}></img>
            <div style={{marginTop: "100px"}} className='auth-frame'>
                <div className='logo'>PIZZA<span style={{color: "firebrick"}}>HUBâ„¢</span></div>
                <input id='email-box' className='user-info' type={"email"} placeholder="Email..."></input>
                <div className='password-container'> 
                    <img onClick={handleClick} id='eye-btn-1' src={require("..\\src\\components\\images\\eye_icon.png")} className='eye-btn'></img>            
                    <input onChange={handlePasswordChange} style={{position: "absolute", margin: 0, left: "15%"}} id='password-box' className='user-info' type={"password"} placeholder="Password..."></input>
                </div>
                <div className='password-container'> 
                    <img onClick={handleClick2} id='eye-btn-1' src={require("..\\src\\components\\images\\eye_icon.png")} className='eye-btn'></img>            
                    <input onChange={handlePasswordConfirmChange} style={{position: "absolute", margin: 0, left: "15%"}} id='password-box2' className='user-info' type={"password"} placeholder="Password..."></input>
                </div>
                <div style={{width: "50%"}} className='submit-btn'>Submit Changes</div>
            </div>
        </div>
        );
}

export default AccountPage;
