import { relative } from 'path';
import { Link } from 'react-router-dom';
import { useEffect, useState } from "react";
import '../auth.css'




const LoginPage = () => {
    const [inputType, setInputType] = useState<string>('password');

    const handleClick = () => {
      setInputType(inputType === 'password' ? 'text' : 'password');
    };
    return(
        <div className="banner-background">
            <img style={{width: "100%", height: "800px", position: "absolute", zIndex: -1}} src={require("..\\src\\components\\images\\wooden_background.jpg")}></img>
            <div className='auth-frame'>
                <div className='logo'>PIZZA<span style={{color: "firebrick"}}>HUBâ„¢</span></div>
                <input id='email-box' className='user-info' type={"email"} placeholder="Email..."></input>
                <div className='password-container'> 
                    <img id='eye-btn-1' src={require("..\\src\\components\\images\\eye_icon.png")} className='eye-btn'></img>            
                    <input style={{position: "absolute", margin: 0, left: "15%"}} id='password-box' className='user-info' type={inputType} placeholder="Password..."></input>
                </div>
                <Link to={'/register'} className='page-link'>I don't have an account</Link>
            </div>
        </div>
        );
}

export default LoginPage;
