import { relative } from 'path';
import { Link } from 'react-router-dom';
import { useEffect, useState, useRef  } from "react";
import '../auth.css';
import axios from "axios";
import CryptoJS from 'crypto-js';

const encryptData = (data: string): string => {
  const key = "CSharpWebAppSpecialKEY555!";
  const iv = CryptoJS.lib.WordArray.random(16);
  const encrypted = CryptoJS.AES.encrypt(data, key, { iv });
  return `${encrypted.toString()}|${iv.toString()}`;
};

const login = async (email: string, password: string) => {
  const encryptedEmail = encryptData(email);
  const encryptedPassword = encryptData(password);
  const data = { email: encryptedEmail, password: encryptedPassword };
  try {
    const response = await axios.post(" 'https://localhost:7048/User'", data);
    const token = response.data.token;
    // store the token in localStorage or a cookie
  } catch (error) {
    console.error(error);
  }
};





const LoginPage = () => {
    const [inputType, setInputType] = useState<string>('password');
    const inputRef = useRef<HTMLInputElement>(null);

    const handleClick = () => {
      setInputType(inputType === 'password' ? 'text' : 'password');
      console.log(`Input value is: ${inputRef.current?.value}`);
    };

    return(
        <div className="banner-background">
            <img style={{width: "100%", height: "800px", position: "absolute", zIndex: -1}} src={require("..\\src\\components\\images\\wooden_background.jpg")}></img>
            <div className='auth-frame'>
                <div className='logo'>PIZZA<span style={{color: "firebrick"}}>HUBâ„¢</span></div>
                <input id='email-box' className='user-info' type={"email"} placeholder="Email..."></input>
                <div className='password-container'> 
                    <img onClick={handleClick} id='eye-btn-1' src={require("..\\src\\components\\images\\eye_icon.png")} className='eye-btn'></img>            
                    <input style={{position: "absolute", margin: 0, left: "15%"}} id='password-box' className='user-info' type={inputType} placeholder="Password..."></input>
                </div>
                <Link to={'/register'} className='page-link'>I don't have an account</Link>
                <div className='submit-btn'>Submit</div>

            </div>
        </div>
        );
}

export default LoginPage;
