import { Link } from 'react-router-dom';
import { useEffect, useState, useRef  } from "react";
import axios from "axios";
import { parseCommandLine } from 'typescript';


interface IUserItem{
    id: number,
    email: string,
    firstName: string,
    lastName: string,
}


  
  
  const AccountPage = () => {
      const [inputType, setInputType] = useState<string>('password');
      const [emailValue, setEmailValue] = useState<string>('');
      const inputRef = useRef<HTMLInputElement>(null);

      const [user, setUser] = useState<IUserItem>();
      
      
      const handleEmailChange = (event: React.ChangeEvent<HTMLInputElement>) => {
          setEmailValue(event.target.value);
        };




        const request = { data: localStorage.getItem('JwtToken') };
        async function GetUser()
        {
            const _response = await axios.post("https://localhost:7048/GetUser", request);
            const _user: IUserItem = _response.data;
            console.log(_user);
            setUser(_user);
        }
        useEffect(() => {
            GetUser();
            setEmailValue("" + user?.email);
          }, []);
        const handleSubmit = () => {

        };
  
      return(
        
          <div style={{height: "800px"}} className="banner-background">
              <img style={{width: "100%", height: "800px", position: "absolute", zIndex: -1}} src={require("..\\src\\components\\images\\wooden_background.jpg")}></img>
              <div style={{marginTop: "0" }} className='auth-frame'>
                  <div className='logo'>PIZZA<span style={{color: "firebrick"}}>HUBâ„¢</span></div>
                  <div style={{fontSize: "35px"}}>Email</div>
                  <input onChange={handleEmailChange} id='email-box' className='user-info' type={"email"} placeholder="Email..."></input>
                  <div style={{fontSize: "35px"}}>First Name</div>
                  <input id='email-box' className='user-info' type={"text"} placeholder="John..."></input>
                  <div style={{fontSize: "35px"}}>Last Name</div>
                  <input id='email-box' className='user-info' type={"text"} placeholder="Smith..."></input>
                  <div style={{backgroundColor: "green"}} onClick={handleSubmit} className='submit-btn submit-green'>Submit</div>
                  <div onClick={handleSubmit} className='submit-btn'>Log Out</div>
                  <div style={{width: "60%"}} onClick={handleSubmit} className='submit-btn'>Change Password</div>
              </div>
          </div>
          );
  }
  
  export default AccountPage;
